  var verodata2017indeksi = (veroTietojenVuodet.length - 1) * solujenLkmPerVuosi

  for (let i = verodata2017indeksi + sarakeNro, j = 0; i < solujenLkmPerVuosi*veroTietojenVuodet.length; i+=6, j++){
      taulukko[j] = verotiedot[i];
  }
  return taulukko;
}

